/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode(int x) { val = x; }
 * }
 */
class Solution {
    public ListNode mergeKLists(ListNode[] lists) {
        if(lists.length == 0)return null;
        List<ListNode> pool = new ArrayList<ListNode>(Arrays.asList(lists));
        while(pool.size() > 1){
            pool = merge(pool);
        }
        return pool.get(0);
    }
    private List<ListNode> merge(List<ListNode> pool){
        List<ListNode> res = new ArrayList<ListNode>();
        for(int i = 0; i< pool.size(); i+=2){
            if(i == pool.size()-1){
                res.add(pool.get(i));
                break;
            }
            res.add(mergeTwoLists(pool.get(i), pool.get(i+1)));
        }
        return res;
    }
    private ListNode mergeTwoLists(ListNode list1, ListNode list2){
        if(list1 == null) return list2;
        if(list2 == null) return list1;
        ListNode dummy = new ListNode(0), cur = dummy;
        while(list1 != null && list2 != null){
            if(list1.val < list2.val)
            {
                cur.next = list1;
                list1 = list1.next;
            }
            else{
                cur.next = list2;
                list2 = list2.next;
            }
            cur = cur.next;
        }
        if(list1 == null){
            cur.next = list2;
        }
        else{
            cur.next = list1;
        }
        return dummy.next;
    }
}
